MovieClip.prototype.activateScroll = function(_text, _backgroundColor, _barcolor, _maskWidth, _maskHeight) {	this._text = _text;	this.indexHeadline = _text.indexOf("|");	this._barcolor = _barcolor;	this._backgroundColor = _backgroundColor;	this._maskWidth = _maskWidth;	this._maskHeight = _maskHeight;	// create Holder-Clip with Textfield	var _scrollText = this.createEmptyMovieClip("_scrollText", this.getNextHighestDepth());	_scrollText.createTextField("_txt_Holder", this._parent.getNextHighestDepth(), 0, 0, this._maskWidth, this._maskHeight);	// format headline text	_scrollText._myFontBold = new TextFormat();	_scrollText._myFontBold.font = "Verdana,Geneva,Arial, _sans";	_scrollText._myFontBold.color = 0xFFFFFF;	_scrollText._myFontBold.size = 10;	_scrollText._myFontBold.bold = true;	_scrollText._myFontBold.leftMargin = 5;	_scrollText._myFontBold.rightMargin = 5;	// format bodytext	_scrollText._myFont = new TextFormat();	_scrollText._myFont.font = "Verdana,Geneva,Arial, _sans";	_scrollText._myFont.color = 0xFFFFFF;	_scrollText._myFont.size = 10;	_scrollText._myFont.leftMargin = 5;	_scrollText._myFont.rightMargin = 5;	// apply format to textfield	_scrollText._txt_Holder.restrict = "a-zA-Z0-9 !'";	_scrollText._txt_Holder.condenseWhite = true;	_scrollText._txt_Holder.wordWrap = true;	_scrollText._txt_Holder.autoSize = true;	_scrollText._txt_Holder.text = this._text;	_scrollText._txt_Holder.setTextFormat(_scrollText._myFont);	// reformat the text -> headline	if (this.indexHeadline) {		_scrollText._txt_Holder.setTextFormat(0, this.indexHeadline, _scrollText._myFontBold);		_scrollText._txt_Holder.replaceText(this.indexHeadline, this.indexHeadline+1, newline);	}	// set background to textfield	if (this._backgroundColor) {		_scrollText._txt_Holder.background = false;		//_scrollText._txt_Holder.backgroundColor = this._backgroundColor;	}	// add scrollbar if text_height > mask	if (_scrollText._txt_Holder._height>=this._maskHeight) {		// init scroll text from bottom		_scrollText._txt_Holder._y = this._maskHeight;		var _mask = this.createEmptyMovieClip("_scrollMask", this.getNextHighestDepth());		// draw Mask		_mask.beginFill(0x000000, 100);		_mask.moveTo(0, 0);		_mask.lineTo(this._maskWidth, 0);		_mask.lineTo(this._maskWidth, this._maskHeight);		_mask.lineTo(0, this._maskHeight);		_mask.lineTo(0, 0);		_mask.endFill();		_scrollText.setMask(_mask);		// draw scrollbar		var _bar = this.createEmptyMovieClip("_scrollBar", this.getNextHighestDepth());		_bar.beginFill(_barcolor, 100);		_bar.moveTo(0, 0);		_bar.lineTo(5, 0);		_bar.lineTo(5, Math.floor(_mask._height/(_scrollText._txt_Holder._height/_mask._height)));		_bar.lineTo(0, Math.floor(_mask._height/(_scrollText._txt_Holder._height/_mask._height)));		_bar.lineTo(0, 0);		_bar.endFill();		_bar._x = _mask._width;		// methods		_bar.onPress = function() {			startDrag(this, false, _mask._width, 0, _mask._width, _mask._height-_bar._height);		};		_bar.onRelease = _bar.onReleaseOutside =function (){			stopDrag();		};		_scrollText.onEnterFrame = function() {			this._txt_Holder._y += ((-_bar._y*(this._txt_Holder._height/_mask._height))-this._txt_Holder._y)/10;		};	}};